import React, { useState } from "react";
import styles from "./index.module.css";

export interface IInventory {
    date: Date,
    quantity: number,
}

export interface ISegmentProps {
    data: IInventory,
    index: number
}



const Segment: React.FC<ISegmentProps> = ({data, index}): JSX.Element => {
    const month = data.date.toLocaleString("en-US", {month:"long"});
    const day = data.date.toLocaleDateString("en-US", {day:"2-digit"});
    const year = data.date.getFullYear();
    const displayDate = `${day}-${month}-${year}`

    const [mouseEntered, setMouseEntered] = useState(false);

    const mouseEnterHandler = (event: React.MouseEvent) => {
        setMouseEntered(true);
    }
    const mouseLeaveHandler = (event: React.MouseEvent) => {
        setMouseEntered(false);
    }

    return (
        <div className={`${styles["segment__container"]}
            ${styles["segment--bordered"]} ${styles["segment--now"]}`}
            onMouseEnter={mouseEnterHandler}
            onMouseLeave={mouseLeaveHandler}
        >
            {mouseEntered && (<p>{displayDate}</p>)}
            <p className={styles["segment__text"]}>{data.quantity}</p>
        </div>
    )
};

export default Segment;
