import React from "react";
import Segment, { IInventory } from "../Segment";
import styles from "./index.module.css"


export interface ISegmentsProps {
    data: IInventory[]
}

const Segments: React.FC<ISegmentsProps> = ({data}) => {

    return (
        <div className={styles["segments__container"]}>
            {data?.length > 0 ? 
            (data.map((element, key) => (
                <Segment
                    date={element.date}
                    quantity={element.quantity} 
                    index={data.indexOf(element)}
                    key={key}
                />
            ))) :
            (<h2 className={styles["segments__text--novalue"]}>{data[0].quantity < 0 ? "Please enter a valid entry" : "Please enter desired quantity"}</h2>)
        }
        </div>
    );
}

export default Segments;
