import React from "react";
import styles from "./index.module.css";

export interface IInventory {
    date: Date,
    quantity: number,
}



const Segment: React.FC<IInventory> = ({date, quantity}): JSX.Element => {
    const month = date.toLocaleString("en-US", {month:"long"});
    const day = date.toLocaleDateString("en-US", {day:"2-digit"});
    const year = date.getFullYear();
    const displayDate = `${day}-${month}-${year}`
    console.log(date, quantity);
    if (!date || !quantity || quantity === 0) {
        return (
            <div className={styles["segment__container"]}>
                <h2 className={styles["segment__text--novalue"]}>Please enter values</h2>
            </div>
        )
    }

    return (
        <div className={`${styles["segment__container"]} ${styles["segment--bordered"]} ${styles["segment--now"]}`}>
            <p>{displayDate}</p>
            <p className={styles["segment__text"]}>{quantity}</p>
        </div>
    )
};

export default Segment;
