import React, { useState } from 'react';
import Title from './Components/Title';
import {IInventory} from './Components/Segment';
import Segments from './Components/Segments';
import './App.css';
import SearchForm from './Components/SearchForm';


const scheduledDeliveries: IInventory[] = [
  {date: new Date(2022, 6, 8), quantity: 39},
  {date: new Date(2022, 6, 9), quantity: 10},
  {date: new Date(2022, 6, 10), quantity: 0},
  {date: new Date(2022, 6, 11), quantity: 31},
  {date: new Date(2022, 6, 12), quantity: 9},
  {date: new Date(2022, 6, 13), quantity: 500},
  {date: new Date(2022, 6,14), quantity: 74}
];

function App() {
  const [inputValue, setInputValue] = useState<number>(0);

  const searchInputHandler = (input: number): void => {
    setInputValue(input);
  }

  const calculateDeliveries = (qty: number) => {
    let deliveryQty = qty;
    let newSchedule: IInventory[] = [];

    for (const item of scheduledDeliveries) {
      if (deliveryQty >= item.quantity) {
        newSchedule.push({date: item.date, quantity: item.quantity});
        deliveryQty = deliveryQty - item.quantity;
      }
      if (deliveryQty <= item.quantity) {
        newSchedule.push({date: item.date, quantity: deliveryQty});
        break;
      }
    }
    console.log(newSchedule);
  }

  calculateDeliveries(41);

  return (
    <div className="App">
     <Title />
     <Segments data={scheduledDeliveries}/>
     <SearchForm handleInputValue={searchInputHandler} />

    </div>
  );
}

export default App;
